import {useState} from 'react';


const Search = ({handleSearch}) => {
    const [inputValue,setInputValue] = useState();

    return (

        <form onSubmit={(e) =>{
            e.preventDefault()

             handleSearch(inputValue)}}>
            <input type="text" 
            placeholder="Search..." 
            value={inputValue}
            onChange={()=> {setInputValue(e.target.value)}}/>
            <button >Search</button>

        </form>
    )
}
export default Search;
